descriptor.controllerScript:
var timoutValue=60000;
let first = 0;
self.detectLastDate = function(tbDatasource, baseInd){
    var lastDate=0;
    var dtklen = tbDatasource.dataKeys.length;
    for (var a = 0; a < dtklen; a++) 
    {
        var cellData = self.ctx.data[dtklen*baseInd+a];
        if (cellData && cellData.data && cellData.data.length > 0) 
        {
            var tvPair = cellData.data[cellData.data.length - 1];
            var value = tvPair[1];
            var ts = tvPair[0];
            if(ts>lastDate)lastDate = ts;
        }
    }

    return lastDate; 
}

self.sensSize = function(tbDatasource, baseInd){
    var lineSize=1;
    var dtklen = tbDatasource.dataKeys.length;

    for (var a = 0; a < dtklen; a++) 
    {
        var cellData = self.ctx.data[dtklen*baseInd+a];
        if (cellData && cellData.data && cellData.data.length > 0) 
        {
            var tvPair = cellData.data[cellData.data.length - 1];
            var value = tvPair[1];
            var ts = tvPair[0];
            var tsnow = Date.now();
            var diff = tsnow - ts ;

            if (ts == tbDatasource.lastDate && diff<timoutValue){ 
               lineSize*=(a+1);
            } 
        }

    }
    return lineSize;

}
function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}

self.onInit = function() 
{
    first = 0;
}

 async function init() 
{
    for (var i = 0; i < self.ctx.datasources.length; i++) {

        var tbDatasource = self.ctx.datasources[i];
        tbDatasource.lastDate = self.detectLastDate(tbDatasource,i);
        tbDatasource.multiLine=self.sensSize(tbDatasource,i);

        tbDatasource.gauges = [];
        //tbDatasource.dataIndex = [];

        var datasourceId = 'tbDatasource' + i;
        self.ctx.$container.append(
            "<span id='" + datasourceId +
            "' class='tbDatasource-container'></span>"
        );
        var datasourceContainer = $('#' +datasourceId,self.ctx.$container);
       //datasourceContainer.append(
       //    "<div class='tbDatasource-title'>" +tbDatasource.name +  "</div>"
       //);
       var datasourceTitleCell = $('.tbDatasource-title',datasourceContainer);
       tbDatasource.datasourceTitleCell= datasourceTitleCell;
        var tableId = 'table' + i;
        datasourceContainer.append(
            "<table id='" + tableId + "' class='tbDatasource-table'>  </table>" );
        var table = $('#' + tableId, self.ctx.$container);


        var dtklen = tbDatasource.dataKeys.length;

        for (var a = 0; a < dtklen; a++) 
        {
            var cellData = self.ctx.data[i*dtklen+a];

            if (cellData && cellData.data && cellData.data.length > 0) 
            {

                var tvPair = cellData.data[cellData.data.length - 1];
                var value = tvPair[1];
                var ts = tvPair[0];
                var tsnow = Date.now();
               var diff = tsnow - ts ;

                if (ts != tbDatasource.lastDate || diff>=timoutValue){ 
                    tbDatasource.gauges.push(0);
                    continue;
                } 
            }else{
                tbDatasource.gauges.push(0);
                continue;
            }


             var dataKey = self.ctx.datasources[0].dataKeys[a];

            var gaugeId = 'gauge' + i*dtklen+a;

            var line = "<tr ><td> <canvas height='20' id='"+ gaugeId+"'></canvas> </td> </tr>";


            table.append(line);
            //tbDatasource.dataIndex.push(a);

            var gauge = new TbCanvasDigitalGauge(self.ctx,gaugeId); 
            tbDatasource.gauges.push(gauge);

            var opt = gauge.gauge.options;
            opt.minValue = dataKey.settings.axisMin; 
            opt.maxValue = dataKey.settings.axisMax; 
            opt.animation=false;
            opt.units=true;
            opt.symbol=" "+dataKey.units;

            opt.hideMinMax = false;
            opt.hideValue = false;
            opt.title = dataKey.label;
            opt.valueDec=dataKey.decimals;
            opt.gaugeColor = "#ddd";
            opt.colorMinMax = "#666";
            opt.colorTitle = dataKey.color;
            opt.colorValue = dataKey.color;
            opt.colorLabel = dataKey.color;
            opt.levelColors[0] = dataKey.color;
            opt.colorsRange[0].rgbString= dataKey.color;


        }
    }
}

self.onDataUpdated = function() 
{
    first++;
    if(first<2 ){ return;}

    if(first==2 )
    { 
        init(); 
        self.onResize();
        self.ctx.detectChanges(true);
    }

    for (var i = 0; i < self.ctx.datasources.length; i++) {

        var tbDatasource = self.ctx.datasources[i];
        tbDatasource.lastDate = self.detectLastDate(tbDatasource,i);
        var calcMultiLine = self.sensSize(tbDatasource,i);

            if(tbDatasource.multiLine!=calcMultiLine){

                self.ctx.$container.empty();
                first=1;
                break;
            }
        for (var a = 0; a < tbDatasource.gauges.length; a++) {
            if(tbDatasource.gauges[a]==0){
                continue;
            }

            var dtklen = tbDatasource.dataKeys.length;
            var cellData = self.ctx.data[i*dtklen+ a];

            if (cellData && cellData.data && cellData.data.length > 0) {
                var tvPair = cellData.data[cellData.data.length - 1];
                var value = tvPair[1];

                tbDatasource.gauges[a].gauge.value = (value);
                tbDatasource.gauges[a].gauge.update();

            }
        }
    }

    function isNumber(n) {
        return !isNaN(parseFloat(n)) && isFinite(n);
    }
}

self.onResize = function() {
    for (var i = 0; i < self.ctx.datasources.length; i++) {
        var tbDatasource = self.ctx.datasources[i];
        for (var a = 0; a < tbDatasource.gauges.length; a++) {
            if(tbDatasource.gauges[a]==0){
                continue;
            }
            var opt = tbDatasource.gauges[a].gauge.options;
            opt.width=Math.min( self.ctx.height, self.ctx.width)*90/100;
            opt.height = opt.width*50/100;
        }
        var datasourceTitleFontSize = self.ctx.height / 8;
        if (self.ctx.width / self.ctx.height <= 1.5) {
            datasourceTitleFontSize = self.ctx.width / 12;
        }
        datasourceTitleFontSize = Math.min( datasourceTitleFontSize, 20);

        tbDatasource.datasourceTitleCell.css('font-size', datasourceTitleFontSize +'px');
    }
}
self.getSettingsSchema = function() {
    return TbCanvasDigitalGauge.settingsSchema;
}
self.getDataKeySettingsSchema = function() {
    return TbFlot.datakeySettingsSchema(true, 'graph');
}
self.onDestroy = function() {}

