let first = 0;
function findLastValues () {

    for(let i=0;i<self.ctx.datasources[0].dataKeys.length;i++)
        {
           var cellData = self.ctx.data[i];
            if (cellData.data.length > 0) 
            {
                self.ctx.defaultSubscription.legendData.keys[i].dataKey.inLegend = true;
            }
            else 
            {

                self.ctx.defaultSubscription.legendData.keys[i].dataKey.inLegend = false;
            }
        }
}
self.onInit = function() 
{
    self.ctx.flot = new TbFlot(self.ctx);
    self.ctx.detectChanges(true);
    first = 0;
}
self.onDataUpdated = function()
{
    if(first<10 ){first++;  }
    if(first==2 ){ 
        findLastValues(); 
        self.ctx.detectChanges(true);
    }
    self.ctx.flot.update();
}

self.onResize = function() {
