descriptor.controllerScript:
let timeoutSec = 5; 
async function getDatasourceKeys (device) {
    let end =0;
    let cnt = 0;
    let entityService = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('entityService'));
    let datasource = device.id;

    entityService.getEntityKeys({entityType: datasource.entityType, id: datasource.id}, '', 'timeseries').subscribe(
        function(data){
            keys = "";
            for(let a=0; a<data.length; a++)
            {
                keys += data[a].toString();
                if(a+1 != data.length)keys += ",";
            }
            end=1;
    });
    while(end==0)
    {
        cnt++;
        if(cnt>timeoutSec*10)break;
        await sleep(100);
    }
    if(end>0){ return keys;}
    else{ return null;}
}

async function deleteData (device) {
    let end =0;
    let cnt = 0;

    let attributeService = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('attributeService'));
    let datasource = device.id;

    let str =keys+"&startTs="+(device.createdTime).toString() +
                "&endTs="+(Date.now()-self.ctx.settings.olderThanDays*24*60*60*1000).toString(); 

    attributeService.deleteEntityTimeseries
    (   {entityType: datasource.entityType, id: datasource.id},
        [{  key:str  }],
        false
        ).subscribe(
            function(data)
            {
                 end=1;
            });
    while(end==0)
    {
        cnt++;
        if(cnt>timeoutSec*10)break;
        await sleep(100);
    }
    if(end>0){ return 1;}
    else{ return null;}
}

async function getDeviceList (page, pageSize) {
    let end =0;
    let cnt = 0;
    let result;

    let deviceService = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('deviceService'));

    var pageLink = self.ctx.pageLink();
    pageLink.page = page;
    pageLink.pageSize=pageSize;

    deviceService.getTenantDeviceInfos( pageLink ).
    subscribe(
    async function(data)
    {
        end=1;
        result = data;
    });
    while(end==0)
    {
        cnt++;
        if(cnt>timeoutSec*10)break;
        await sleep(100);
    }
    if(end>0){ return result;}
    else{ return null;}
}

async function  deleteOldTimeseries () {
    let hasNext = true;

    for(let b=0; hasNext;b++)
    {
        let deviceList = await getDeviceList(b,100);
        if(!deviceList)break;
        hasNext = deviceList.hasNext;

        console.log(deviceList);
        for(let a=0;a<deviceList.data.length;a++)
        {
            console.log(deviceList.data[a].name);
            ready = 0;

            keys = await getDatasourceKeys(deviceList.data[a]);
            if(keys==null || keys=="")continue;
            console.log(keys);

            result = await deleteData(deviceList.data[a]);
            if(result>0)console.log("Deleted!");
        }
    }
}

function sleep(ms) {
  return new Promise(resolve => setTimeout(resolve, ms));
}


self.onInit = function () 
{
    self.ctx.$scope.showAlert = async function() {
        if (self.ctx.settings.olderThanDays>0) {
            console.log("Start delete older than "+ self.ctx.settings.olderThanDays+" days");

            await deleteOldTimeseries();

            console.log("end delete");
            window.alert("Deleted Old Timeseries.");  
        }else{
            let msg = "Enter a value higher than 0";
            console.log(msg);
             window.alert(msg);  
        }
    };
}
